INSERT INTO `books` (`book_index`, `book_id`, `title`, `author`, `description`, `availability`, `category`, `rating`, `image_url`, `publisher_id`, `published_year`, `online_reading_url`, `physical_location`, `no_of_times`, `reg_time`) VALUES (NULL, 'book014', 'The Computer Music Tutorial', 'Curtis Roads', 'This text provides a step-by-step introduction to the entire field of computer music techniques. Written for nontechnical as well as technical readers, it uses hundreds of charts, diagrams, screen images, and photographs as well as clear explanations to present basic concepts and terms.', '2', 'Computers', '4.3', 'http://books.google.com/books/content?id=nZ-TetwzVcIC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'pub003', '1996', 'http://books.google.com.my/books?id=nZ-TetwzVcIC&printsec=frontcover&dq=%7Bcomputer%7D&hl=&cd=4&source=gbs_api', '2 1 3', '0', CURRENT_TIMESTAMP);
