INSERT INTO `books` (`book_index`, `book_id`, `title`, `author`, `description`, `availability`, `category`, `rating`, `image_url`, `publisher_id`, `published_year`, `online_reading_url`, `physical_location`, `no_of_times`, `reg_time`) VALUES (NULL, 'book020', 'Irrational Numbers', 'George Effinger', 'The death of a pet fish signals an ominous threat of wordwide tragedy... Delta Company \'plays\' out a war light years away... A running back for the Cleveland Browns gives his all to relive a night from his past. In IRRATIONAL NUMBERS, as with much of his work, author George Alec Effinger straddles the line between allegorical fantasy and science fiction. It\'s a vein Effinger mines for a deep, meaningful understanding of human nature. Challenging and disquieting in the way only the best fiction can be, this collection of eight magnificent pieces of fiction will have readers clamoring for more.', '3', 'Fiction', '4.2', 'http://books.google.com/books/content?id=unnWZ7FYTiAC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'pub021', '2011', 'http://books.google.com.my/books?id=unnWZ7FYTiAC&pg=PT4&dq=%7Bcategories:fiction%7D&hl=&cd=1&source=gbs_api', '4 1 3', '0', CURRENT_TIMESTAMP);
