INSERT INTO `books` (`book_index`, `book_id`, `title`, `author`, `description`, `availability`, `category`, `rating`, `image_url`, `publisher_id`, `published_year`, `online_reading_url`, `physical_location`, `no_of_times`, `reg_time`) VALUES (NULL, 'book023', 'Principles of Literary Criticism', 'I. A. Richards', 'Ivor Armstrong Richards was one of the founders of modern literary criticism. He enthused a generation of writers and readers and was an influential supporter of the young T.S. Eliot. \'Principles of Literary Criticism\' was the text that first established his reputation and pioneered the movement that became known as the \'New Criticism\'. Through a powerful presentation of the need to read critically and creatively, with an alertness to the psychological and emotional effects of language, Richards presented a powerful new understanding both of literature and of the role of the reader.', '3', 'Literary', '4.5', 'http://books.google.com/books/content?id=xZtuDns73s4C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', 'pub018', '2001', 'http://books.google.com.my/books?id=xZtuDns73s4C&printsec=frontcover&dq=%7Bliterary%7D&hl=&cd=6&source=gbs_api', '5 1 4', '0', CURRENT_TIMESTAMP);
